class Weather {
    constructor(t) {
        this.city = t
    }
    async getWeather() {
        try {
            const response = await fetch(`https://api.weatherapi.com/v1/forecast.json?key=0ac09128c2b74d6395e60023213011&q=${this.city}&days=3&aqi=yes&alerts=yes`);
            let json = await response.json();
            console.log(json);
            return json;
        }
        catch (err) {
            console.log(err);
        }
    }
    // async getWeather() {
    //     return fetch(`.netlify/functions/weather_api?q=${this.city}`, {
    //         method: "GET"
    //     }).then((t => t.json())).catch((t => console.error(t)))
    // }
    changeLocation(t) {
        this.city = t
    }
}